        public static string GetHtmlcontentTestMultipleChoice(string courseName, string testName, DateTime startdate, DateTime enddate, double pastMarks, List<StudentListOfEmail> students)
        {
            string Response = "<!DOCTYPE html>\r\n<html lang=\"vi\">\r\n" +
                        $"<head>\r\n  <meta charset=\"UTF-8\">\r\n  <title>Thông báo thi</title>\r\n  <style>\r\n" +
                        // ... (your existing styles)
                        $"</style>\r\n</head>\r\n" +
                        $"<body style=\"font-family: sans-serif;\">\r\n  <header style=\"background-color: #000; color: #fff; padding: 10px;\">\r\n" +
                        $"    <img style=\"width: 100px;\" src=\"https://raw.githubusercontent.com/ngomanhson/SEM3-ExamonimyStudent/main/public/assets/img/logo.png?fbclid=IwAR3Q8tz5IlZEyBtTAHKqnC29j1XhHREMPCzhLV4Ph_AeY-dOKBaz-J1Yxxg\" alt=\"Logo FPT\">\r\n" +
        $"    <h1>Exam Notice</h1>\r\n  </header>\r\n" +
        $"<main style=\"font-size: 16px;\">\r\n" +
        $"    <p>\r\n      Dear students,\r\n    </p>\r\n    <p>\r\n      The Training Department announces the following exam schedule:\r\n    </p>\r\n    " +
        $"<table style=\"border-collapse: collapse; width: 75%;\">\r\n      <tr>\r\n        " +
        $"<th style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">Subject</th>\r\n        " +
        $"<th style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">Test</th>\r\n        " +
        $"<th style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">Start Date</th>\r\n        " +
        $"<th style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">End Date</th>\r\n      </tr>\r\n      <tr>\r\n        " +
        $"<td style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">{courseName}</td>\r\n        " +
        $"<td style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">{testName}</td>\r\n        " +
        $"<td style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">{startdate}</td>\r\n        " +
        $"<td style=\"text-align: center; border: 1px solid #ccc; padding: 5px;\">{enddate}</td>\r\n      </tr>\r\n    </table>\r\n    <p>\r\n      " +
        $"Completion requirements:\r\n    </p>\r\n    <ul>\r\n      <li style=\"margin: 10px 0;\">All ASM scores >= {pastMarks}.</li>\r\n    </ul>\r\n    <p>\r\n      " +
        $"Please remember to complete the NSC feedback in the AP system. If you do not do so, you will not be able to view your exam results.\r\n    </p>\r\n    <p>\r\n      " +
        $"Link: <a href=\"https://academic.fptacademy.edu.vn/\">Examonimy Academy - Academic Portal</a>\r\n    </p>\r\n    <p>\r\n      <strong>List of students taking the exam:</strong>\r\n    </p>\r\n" +
        $"<table style=\"border-collapse: collapse; width: 100%;\">\r\n      <tr>\r\n        " +
        $"<th style=\"width: 5%; border: 1px solid #ccc; padding: 5px;\">STT</th>\r\n        " +
        $"<th style=\"border: 1px solid #ccc; padding: 5px;\">Student Code</th>\r\n        " +
        $"<th style=\"border: 1px solid #ccc; padding: 5px;\">Full Name</th>\r\n      </tr>\r\n";

            for (int i = 0; i < students.Count; i++)
            {
                Response += $"<tr>\r\n" +
                    $"  <td style=\"border: 1px solid #ccc; padding: 5px;\">{i + 1}</td>\r\n" +
                    $"  <td style=\"border: 1px solid #ccc; padding: 5px;\">{students[i].student_code}</td>\r\n" +
                    $"  <td style=\"border: 1px solid #ccc; padding: 5px;\">{students[i].fullname}</td>\r\n" +
                    $"</tr>\r\n";
            }

            Response += "</table>\r\n" +
                "<p>\r\n" +
                $"Good luck on your exam!\r\n" +
                "</p>\r\n" +
                "</main>\r\n" +
                "</body>\r\n" +
                "</html>";

            return Response;
        }
    }
